  // Test 2: Check API info structure
  expect(data.data.name).toBe('Frigate Middleware API');
  expect(data.data.version).toBe('1.0.0');
  expect(Array.isArray(data.data.servers)).toBe(true);

  // Test 3: Check endpoints list
  expect(data.data.endpoints).toBeDefined();
  expect(data.data.endpoints.health).toBe('/health');
  expect(data.data.endpoints.cameras).toBe('/api/cameras/*');

  console.log('âœ… Root endpoint passed - API info retrieved');
