// Test cameras list endpoint
const response = await insomnia.send();

// Check if response is successful
if (response.status === 200) {
  const data = response.json;

  // Test 1: Check response structure
  expect(data.success).toBe(true);
  expect(data.message).toContain('Cameras retrieved successfully');

  // Test 2: Check cameras array
  expect(Array.isArray(data.data.cameras)).toBe(true);
  expect(data.data.cameras.length).toBeGreaterThan(0);

  // Test 3: Check expected camera names
  const expectedCameras = ['admin_office', 'employees_01', 'employees_02', 'employees_03', 'employees_04', 'employees_05', 'employees_06', 'employees_07', 'employees_08', 'meeting_room', 'reception'];
  const foundCameras = expectedCameras.filter(cam => data.data.cameras.includes(cam));
  expect(foundCameras.length).toBeGreaterThan(0);

  // Test 4: Check count field
  expect(data.data.count).toBe(data.data.cameras.length);

  console.log(`âœ… Cameras list passed - Found ${data.data.cameras.length} cameras`);
  console.log(`Cameras: ${data.data.cameras.join(', ')}`);
} else {
  throw new Error(`Cameras list failed with status ${response.status}`);
}

// Test recent recordings endpoint
const response = await insomnia.send();

// Check if response is successful
