  const data = response.json;

  // Test 1: Check response structure
  expect(data.success).toBe(true);
  expect(data.message).toContain('Recent recordings retrieved successfully');

  // Test 2: Check recordings array
  expect(Array.isArray(data.data.recordings)).toBe(true);

  // Test 3: Check recording structure (if any exist)
  if (data.data.recordings.length > 0) {
    const recording = data.data.recordings[0];
    expect(recording.id).toBeDefined();
    expect(recording.camera).toBeDefined();
    expect(recording.startTime).toBeDefined();
    expect(recording.endTime).toBeDefined();
    expect(recording.videoUrl).toBeDefined();

    // Test 4: Check video URL format
    expect(recording.videoUrl).toMatch(/^http:\/\/10\.0\.20\.6:8000\//);
  }

  // Test 5: Check pagination info
  expect(data.data.pagination).toBeDefined();
  expect(data.data.pagination.totalResults).toBe(data.data.recordings.length);

  console.log(`âœ… Recent recordings passed - Found ${data.data.recordings.length} recordings`);
} else {
  throw new Error(`Recent recordings failed with status ${response.status}`);
}

