// Test media URLs endpoint
const response = await insomnia.send();

// Check if response is successful
if (response.status === 200) {
  const data = response.json;

  // Test 1: Check response structure
  expect(data.success).toBe(true);
  expect(data.message).toContain('Media URL test completed');

  // Test 2: Check video server URL
  expect(data.data.video_server_url).toBe('http://10.0.20.6:8000');

  // Test 3: Check tests array
  expect(Array.isArray(data.data.tests)).toBe(true);

  // Test 4: Check test structure (if any exist)
  if (data.data.tests.length > 0) {
    const test = data.data.tests[0];
    expect(test.type).toBeDefined();
    expect(test.status).toBeDefined();

    // Test 5: Check URL format
    if (test.thumbnail_url) {
      expect(test.thumbnail_url).toMatch(/^http:\/\/10\.0\.20\.6:8000\//);
    }
    if (test.video_url) {
      expect(test.video_url).toMatch(/^http:\/\/10\.0\.20\.6:8000\//);
