// Test video stream endpoint
const response = await insomnia.send();

// Check if response is successful
if (response.status === 200) {
  // Test 1: Check content type
  const contentType = response.headers['content-type'] || response.headers['Content-Type'];
  expect(contentType).toContain('video/mp4');

  // Test 2: Check content length
  const contentLength = response.headers['content-length'] || response.headers['Content-Length'];
  if (contentLength) {
    expect(parseInt(contentLength)).toBeGreaterThan(0);
  }

  // Test 3: Check accept ranges header
  const acceptRanges = response.headers['accept-ranges'] || response.headers['Accept-Ranges'];
  expect(acceptRanges).toBe('bytes');

  console.log('âœ… Video stream test passed - Video file accessible');
} else if (response.status === 206) {
  // Partial content is also acceptable for range requests
  console.log('âœ… Video stream test passed - Partial content (range request)');
} else {
  throw new Error(`Video stream test failed with status ${response.status}`);
}

